mapper.delete:
DELETE
FROM post_reaction
WHERE post_id = #{postId}
AND member_id = #{memberId}
AND type = #{type}

mapper.select:
SELECT *
FROM post_reaction
WHERE post_id = #{postId}
AND member_id = #{memberId}
LIMIT 1

SELECT COUNT(*)
FROM post_reaction
WHERE post_id = #{postId}
AND type = #{type}

mapper.update:
UPDATE post
SET hot = true
WHERE id = #{postId}
